import CommentItem from "./CommentItem";
import Comment from "../types/Comment";
import React, { useState } from "react";
import { Button } from "@mui/material"; // Import Button from your UI library

type Props = {
    styled: boolean;
};

const BasicCommentList: React.FC<Props> = ({ styled }: Props) => {
    const [comments, setComments] = useState<Comment[]>([
        // Existing comments
        {
            body: "Any fool can write code...",
            author: "Benedict",
            timestamp: new Date(2022, 10, 28, 10, 33, 30),
        },
        // Other existing comments...
    ]);

    const addComment = () => {
        const newComment: Comment = {
            body: "New Comment",
            author: "User",
            timestamp: new Date(),
        };
        setComments([...comments, newComment]);
    };

    return (
        <div>
            <ul>
                {comments.map((comment, index) => (
                    <CommentItem comment={comment} styled={styled} key={index.toString()} />
                ))}
            </ul>
            <Button variant="contained" color="primary" onClick={addComment}>
                Add Comment
            </Button>
        </div>
    );
};

export default BasicCommentList;
