import PostItem from "./PostItem";
import axios from "axios";
import Posts from "../types/Post";
import React, { useState, useEffect } from "react";
import { Button, TextField } from "@mui/material"; // Import Button from your UI library

type Props = {
    styled: boolean;
};

const BasicPostsList: React.FC<Props> = ({ styled }: Props) => {
    const [Posts, setPost] = useState<Posts[]>([]);
    const [newPosts, setNewPosts] = useState<string>("");

    useEffect(() => {
        axios.get("http://localhost:8000/posts")
        .then((response) => {
            setPost(response.data);
        })
        .catch((error) => {
            console.error('There was an error!', error);
        });
    }, []);

    const handleNewPostsChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setNewPosts(event.target.value);
    }

    const addPosts = () => {
        if (newPosts.trim() !== "") {
            const Posts: Posts = {
                body: newPosts,
                author: "User",
                timestamp: new Date(),
            };
            setPost([...Posts, Post]);
            setNewPosts("");
        }
    };

    return (
        <div>
            <ul>
                {Posts.map((Posts, index) => (
                    <PostItem Posts={Posts} styled={styled} key={index.toString()} />
                ))}
            </ul>
            <TextField
                label="New Post"
                variant="outlined"
                onChange={handleNewPostsChange}
                value={newPosts}
            />
            <Button variant="contained" color="primary" onClick={addPosts}>
                Add Posts
            </Button>
        </div>
    );
};

export default BasicPostsList;
