import PostItem from "./PostItem";
import axios from "axios";
import Post from "../types/Post";
import React, { useState, useEffect } from "react";
import { Button, TextField } from "@mui/material"; // Import Button from your UI library

type Props = {
    styled: boolean;
};

const BasicPostsList: React.FC<Props> = ({ styled }: Props) => {
    const [Posts, setPost] = useState<Post[]>([]);
    const [newPost, setNewPosts] = useState<string>("");

    useEffect(() => {
        axios.get("http://localhost:8000/posts")
        .then((response) => {
            setPost(response.data);
        })
        .catch((error) => {
            console.error('There was an error!', error);
        });
    }, []);

    const handleNewPostChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setNewPosts(event.target.value);
    }

    const handleNewPostSubmit = () => {
        // Replace 'http://localhost:5000/posts' with your actual API endpoint
        axios.post('http://localhost:5000/posts', {
            body: newPost,
            author: 'Author Name', // Replace with the actual author name
            timestamp: new Date(),
        })
        .then(response => {
            // Add the new comment to the state
            setPosts(prevComments => [...prevComments, response.data]);
            // Clear the input field
            setNewPost('');
        })
        .catch(error => {
            console.error('Error adding comment', error);
        });
    };

    return (
        <div>
            <ul>
                {Posts.map((post, index) => (
                    <PostItem Post={post} styled={styled} key={index.toString()} />
                ))}
            </ul>
            <TextField
                label="New Post"
                variant="outlined"
                onChange={handleNewPostsChange}
                value={newPost}
            />
            <Button variant="contained" color="primary" onClick={addPost}>
                Add Posts
            </Button>
        </div>
    );
};

export default BasicPostsList;
