import React, {createContext, useState, ReactNode} from 'react';

interface UserContextType {
    username: string;
    setUsername: (username: string) => void;
    isLoggedIn: boolean;
    setIsLoggedIn: (isLoggedIn: boolean) => void;
};

const UserContext = createContext<UserContextType>({
    username: "",
    setUsername: () => {},
    isLoggedIn: false,
    setIsLoggedIn: () => {},
});

type Props = {
    children: ReactNode;
};

export const UserProvider: React.FC<Props> = ({children}: Props) => {
    const [username, setUsername] = useState<string>("");
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const login = () => setIsLoggedIn(true);  // Optionally set username here if needed
    const logout = () => {
        setIsLoggedIn(false);
        setUsername("");  // Reset username on logout
    };

    return (
        <UserContext.Provider value={{username, setUsername, isLoggedIn, setIsLoggedIn, logout}}>
            {children}
        </UserContext.Provider>
    );
}

export const useUser = () => React.useContext(UserContext);

export default UserContext;
